declare @table table (\n    [column] xml\n)\n\ninsert @table ([column]) values ('<r><i>1</i><i>2</i><i>3</i></r>')\n\ndeclare @parameter xml\nset @parameter = '<r><i>1</i><i>2</i></r>'\n\n-- Use FLWOR expression to delete nodes\nupdate @table \nset [column].modify('\n    for $id in /r/i\n    let $value := xs:string($id/text())\n    where exists(/r/i[text() = $value])\n    return delete node(//i[text() = $value])')\n\n-- Check the result\nselect * from @table