-- Index on EventTypeCode\nCREATE NONCLUSTERED INDEX IX_EventTable_EventTypeCode ON EventTable(EventTypeCode);\n\n-- Index on EventStatusCode\nCREATE NONCLUSTERED INDEX IX_EventTable_EventStatusCode ON EventTable(EventStatusCode);\n\n-- Composite index for queries involving both EventDate and either EventTypeCode or EventStatusCode.\nCREATE NONCLUSTERED INDEX IX_EventTable_Date_Type_Code \nON EventTable(EventDate, EventTypeCode);  -- or (EventDate, EventStatusCode) depending on query patterns