-- Creating necessary indexes if not already created\nCREATE INDEX idx_topic_date ON wp_pod_tbl_forum (topic_id, date);\nCREATE INDEX idx_reply_topic_date ON wp_pod_tbl_forum (topic_id, date);\n\nSELECT \n    t.id, \n    t.name, \n    m.last_updated AS date, \n    r.reply_count AS replies\nFROM \n    wp_pod_tbl_forum t\nJOIN (\n    SELECT \n        topic_id, \n        MAX(date) AS last_updated\n    FROM \n        wp_pod_tbl_forum\n    GROUP BY \n        topic_id\n) m ON t.id = m.topic_id\nJOIN (\n    SELECT \n        topic_id, \n        COUNT(id) AS reply_count\n    FROM \n        wp_pod_tbl_forum\n    WHERE \n        topic_id != 0\n    GROUP BY \n        topic_id\n) r ON t.id = r.topic_id\nWHERE \n    t.topic_id = 0\nORDER BY \n    m.last_updated DESC \nLIMIT 0, 20;