DECLARE @BinaryData VARBINARY(MAX);\nDECLARE @OutputFileName NVARCHAR(256) = N'C:\path\to\your\output.pdf';\nDECLARE @Stream INT;\n\n-- Assume your table and column are named Files and FileContent respectively\nSELECT TOP 1 @BinaryData = FileContent FROM Files WHERE /* your condition */;\n\nEXEC sp_OACreate 'ADODB.Stream', @Stream OUT;\nEXEC sp_OAMethod @Stream, 'Type=', NULL, 1; -- adTypeBinary\nEXEC sp_OAMethod @Stream, 'Open';  \nEXEC sp_OAMethod @Stream, 'Write', NULL, @BinaryData;\nEXEC sp_OAMethod @Stream, 'SaveToFile', NULL, @OutputFileName, 2; -- adSaveCreateOverWrite\nEXEC sp_OADestroy @Stream;