CREATE TABLE products (\n    product_id INT PRIMARY KEY,\n    name_key VARCHAR(255) NOT NULL, -- Key for referencing localized names\n    description_key VARCHAR(255) NOT NULL -- Key for referencing localized descriptions\n);\n\nCREATE TABLE translations (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    key_name VARCHAR(255) NOT NULL,  -- Matches the keys in products table\n    language_code CHAR(2),           -- ISO-639-1 code (e.g., 'en', 'fr')\n    translated_text TEXT NOT NULL,\n    UNIQUE INDEX idx_key_language (key_name, language_code)\n);