DECLARE @BinaryData VARBINARY(MAX);\nDECLARE @OutputFileName NVARCHAR(256) = 'C:\path\to\your\output.pdf';\nDECLARE @FSO INT;\nDECLARE @FileTextStream TEXT;\n\n-- Assume your table and column are named Files and FileContent respectively\nSELECT TOP 1 @BinaryData = FileContent FROM Files WHERE /* your condition */;\n\nEXEC sp_OACreate 'Scripting.FileSystemObject', @FSO OUT;\nEXEC sp_OAMethod @FSO, 'CreateTextFile', @FileTextStream OUT, @OutputFileName;\nEXEC sp_OAMethod @FileTextStream , 'Write' , NULL, CAST(@BinaryData AS VARCHAR(MAX));\nEXEC sp_OADestroy @FileTextStream;\nEXEC sp_OADestroy @FSO;