CREATE PROCEDURE UpdateMyTable\n    @ID INT,\n    @Name NVARCHAR(50) = NULL,\n    @Email NVARCHAR(100) = NULL\nAS\nBEGIN\n    -- Update only if the Name parameter is provided and not null\n    IF @Name IS NOT NULL\n    BEGIN\n        UPDATE MyTable\n        SET Name = @Name\n        WHERE ID = @ID;\n    END\n\n    -- Similarly, update Email only if it's provided\n    IF @Email IS NOT NULL\n    BEGIN\n        UPDATE MyTable\n        SET Email = @Email\n        WHERE ID = @ID;\n    END\nEND