DECLARE @N INT = 6;\nDECLARE @SQL NVARCHAR(MAX);\nDECLARE @RowID INT = 14;\nDECLARE @ColumnName SYSNAME;\n\n-- Query sys.columns to get the Nth column name\nSELECT TOP 1 @ColumnName = name \nFROM sys.columns \nWHERE object_id = OBJECT_ID('MyTable') \nORDER BY column_id ASC\nOFFSET @N-1 ROWS FETCH NEXT 1 ROW ONLY;\n\n-- Construct and execute dynamic SQL to select the Nth column value\nSET @SQL = N'SELECT ' + QUOTENAME(@ColumnName) + N' FROM MyTable WHERE RowID = @RowID';\nEXEC sp_executesql @SQL, N'@RowID INT', @RowID;