CREATE TABLE SoapMessages (\n    MessageID INT PRIMARY KEY,\n    Content XML NOT NULL\n);\n\n-- Inserting an example SOAP message\nINSERT INTO SoapMessages (MessageID, Content)\nVALUES (1, '<soap:Envelope xmlns:soap="http://schemas.xmlsoap.org/soap/envelope/"><soap:Body><HelloWorld>Hello World from SQL Server 2005!</HelloWorld></soap:Body></soap:Envelope>');\n\n-- Querying XML data\nSELECT \n    Content.value('(/soap:Envelope/soap:Body/HelloWorld/text())[1]', 'NVARCHAR(MAX)') AS MessageContent\nFROM \n    SoapMessages;