-- Assuming the table name is your_table and the column with unique index is unique_column\n-- Let's denote the two values to be swapped as val1 and val2\n\nUPDATE your_table\nSET unique_column = CASE \n                      WHEN unique_column = 'val1' THEN 'temp_val'\n                      WHEN unique_column = 'val2' THEN 'val1'\n                    END\nWHERE unique_column IN ('val1', 'val2');\n\nUPDATE your_table\nSET unique_column = CASE \n                      WHEN unique_column = 'temp_val' THEN 'val2'\n                    END\nWHERE unique_column = 'temp_val';