DELIMITER $$\n\nCREATE PROCEDURE UpdateBenchmark()\nBEGIN\n   DECLARE mean_value DECIMAL(10,4);\n   DECLARE stddev_value DECIMAL(10,4);\n\n   -- Calculate the mean\n   SELECT AVG(stress_value) INTO mean_value FROM health_data;\n\n   -- Calculate the standard deviation\n   SELECT STDDEV(stress_value) INTO stddev_value FROM health_data;\n\n   -- Update the benchmark table with new calculated values\n   UPDATE benchmark SET mean = mean_value, standard_deviation = stddev_value WHERE id = 1;\nEND $$\n\nDELIMITER ;