CREATE TABLE FactCaseMovement (\n         MovementID INT AUTO_INCREMENT PRIMARY KEY,\n         CaseID INT,\n         DepartmentID INT,\n         EventID INT,  -- if using an EventType dimension\n         TimeID INT,\n         ActionTaken VARCHAR(255),  -- or reference this to another action table if standardized actions exist\n         DurationInHours DECIMAL(5, 2),\n         FOREIGN KEY (CaseID) REFERENCES DimCase(CaseID),\n         FOREIGN KEY (DepartmentID) REFERENCES DimDepartment(DepartmentID),\n         FOREIGN KEY (TimeID) REFERENCES DimTime(TimeID)\n     );