DECLARE\n    v_file  UTL_FILE.FILE_TYPE;\n    CURSOR cur_data IS\n        SELECT * FROM your_table; -- Replace with your actual query\nBEGIN\n    -- Open the file for writing\n    v_file := UTL_FILE.FOPEN('OUTPUT_DIR', 'output.txt', 'W');\n\n    FOR rec IN cur_data LOOP\n        UTL_FILE.PUT_LINE(v_file, rec.column1 || ',' || rec.column2 || ',' || rec.column3);\n        -- Adjust the above line according to your table's columns and desired delimiter.\n    END LOOP;\n\n    -- Close the file\n    UTL_FILE.FCLOSE(v_file);\n\nEXCEPTION\n    WHEN OTHERS THEN\n        -- Handle the exception\n        IF UTL_FILE.IS_OPEN(v_file) THEN\n            UTL_FILE.FCLOSE(v_file);\n        END IF;\n        RAISE;\nEND;