SELECT\n    f.featureTitle AS title, \n    f.featureSummary AS body, \n    f.postedOn AS dummy, \n    DATE_FORMAT(f.postedOn, '%M %d, %Y') AS posted, \n    NULL, \n    NULL, \n    s.staffName, \n    f.featureID \nFROM \n    features f\nLEFT JOIN \n    staff s ON f.staffID = s.staffID\nWHERE \n    f.postedOn < (\n        SELECT MAX(postedOn) FROM features\n    )\nORDER BY \n    dummy DESC \nLIMIT 0,15;