-- Setting up tables with DEFERRABLE INITIALLY DEFERRED constraints\nCREATE TABLE question (\n    id SERIAL PRIMARY KEY,\n    text TEXT NOT NULL,\n    correct_answer INTEGER REFERENCES answer(id) DEFERRABLE INITIALLY DEFERRED\n);\n\nCREATE TABLE answer (\n    id SERIAL PRIMARY KEY,\n    text TEXT NOT NULL,\n    question INTEGER REFERENCES question(id) DEFERRABLE INITIALLY DEFERRED\n);