DECLARE @counter int;\nSET @counter = 1;\n\n-- Ensure @tempCount is initialized properly\nDECLARE @tempCount int;\nSELECT @tempCount = COUNT(*) FROM #vals; -- Adjust this line based on how you determine @tempCount\n\nDECLARE @tempID varchar(50);\nDECLARE @tempCat varchar(255);\nDECLARE @tempCatString varchar(5000);\n\n-- Initialize to an empty string, not NULL\nSET @tempID = '';\nSET @tempCat = '';\nSET @tempCatString = '';\n\nWHILE @counter <= @tempCount\nBEGIN\n\n    SET @tempID = (\n    SELECT [Val]\n    FROM #vals\n    WHERE [ID] = @counter);\n\n    -- Ensure tempCat is not null to avoid making the entire string null during concatenation\n    SET @tempCat = ISNULL((SELECT [Description] FROM [Categories] WHERE [ID] = @tempID), '');\n\n    IF LEN(@tempCatString) > 0\n        SET @tempCatString = @tempCatString + '<br/>' + @tempCat;\n    ELSE\n        -- Avoid leading <br/> in the first entry\n        SET @tempCatString = @tempCat;\n\n    PRINT @tempCat; -- Debug statement\n\n    SET @counter = @counter + 1;\n\nEND\n\n-- Output the final concatenated string\nPRINT @tempCatString;