EXEC sp_configure 'show advanced options', 1;\n   RECONFIGURE;\n\n   EXEC sp_configure 'Ad Hoc Distributed Queries', 1;\n   RECONFIGURE;\n\n   -- Check current setting for MSDAORA provider\n   SELECT * FROM sys.configurations WHERE name LIKE '%Ad Hoc%';\n\n   -- To allow ad hoc access to MSDAORA, set DisallowAdhocAccess to 0\n   EXEC sp_MSset_oledb_prop N'MSDAORA', N'DisallowAdHocAccess', 0;\n   RECONFIGURE;