-- First, create a helper variable if your MySQL version supports dynamic SQL for sequences of numbers.\nWITH RECURSIVE num AS (\n    SELECT 1 AS n\n    UNION ALL\n    SELECT n + 1 FROM num WHERE n < 10000000\n)\nINSERT INTO VOUCHER (VOUCHER_NUMBER, BOOK_ID, DENOMINATION) \nSELECT n, b.ID, b.DENOMINATION \nFROM num, BOOK b\nWHERE n BETWEEN b.START_NUMBER AND b.START_NUMBER + b.UNITS - 1;