SELECT char_count.count, s.name as strip, ch.name as character\nFROM \n    (SELECT appearances.character_id, COUNT(*) AS count\n     FROM appearances\n     WHERE date IN (...)  -- Add any additional conditions here if necessary\n     GROUP BY appearances.character_id) AS char_count\nJOIN appearances a ON char_count.character_id = a.character_id\nJOIN strips s ON a.strip_id = s.id\nJOIN characters ch ON a.character_id = ch.id\nWHERE a.date IN (...);  -- Ensure this matches the condition in the subquery