-- Assume a mapping table like this:\nCREATE TABLE ValueMapping (\n    HashValue BINARY(32) PRIMARY KEY,\n    OriginalValue VARCHAR(2000) NOT NULL UNIQUE\n);\n\n-- Function to get or add a hash value in your SQL server\nCREATE FUNCTION dbo.GetOrAddHash(@value NVARCHAR(2000))\nRETURNS BINARY(32)\nAS\nBEGIN\n    DECLARE @hash BINARY(32);\n    SET @hash = HASHBYTES('SHA2_256', @value);\n\n    IF NOT EXISTS (SELECT 1 FROM ValueMapping WHERE HashValue = @hash)\n        INSERT INTO ValueMapping (HashValue, OriginalValue) VALUES (@hash, @value);\n\n    RETURN @hash;\nEND;\n\n-- Example usage in your main table\nALTER TABLE LogTable\nADD BASEURL_HASH BINARY(32),\n    USER_HASH BINARY(32),\n    SERVER_HASH BINARY(32),\n    USERAGENT_HASH BINARY(32);