SET @table_name = 'your_table_name';\nSET @pk_column = 'id'; -- Assuming 'id' is your auto increment column\n\n-- Get the list of columns excluding the primary key\nSELECT GROUP_CONCAT(columns.COLUMN_NAME SEPARATOR ', ')\nINTO @columns\nFROM information_schema.COLUMNS \nWHERE TABLE_SCHEMA=DATABASE() \nAND TABLE_NAME=@table_name \nAND COLUMN_NAME != @pk_column;\n\n-- Construct the SQL statement to clone a record with id = 1\nSET @sql = CONCAT('INSERT INTO ', @table_name, ' (', @columns, ') SELECT ', @columns, ' FROM ', @table_name, ' WHERE ', @pk_column, ' = 1');\n\n-- Execute the dynamic SQL\nPREPARE stmt FROM @sql;\nEXECUTE stmt;\nDEALLOCATE PREPARE stmt;