CREATE OR REPLACE TRIGGER update_product_rating\nAFTER INSERT, UPDATE ON Review\nFOR EACH ROW\nBEGIN ATOMIC\n    DECLARE avg_rating REAL;\n    \n    -- Calculate the average rating for the product\n    SELECT AVG(Rating)\n    INTO avg_rating\n    FROM Review\n    WHERE Productname = NEW.Productname;\n\n    -- Update the Product table with the new average rating\n    UPDATE Product\n    SET Rating = avg_rating\n    WHERE Productname = NEW.Productname;\nEND