SELECT \n    Item.Id AS Item_Id,\n    Item.Section AS Item_Section,\n    Event.Id AS Event_Id,\n    Event.Venue_id,\n    Event.Configuration,\n    Seat.Face_value\nFROM \n    Item\nJOIN \n    Event ON Item.Event_id = Event.Id\nJOIN \n    Seat ON (Event.Venue_id = Seat.Venue_id \n             AND Event.Configuration = Seat.Configuration \n             AND Item.Section = Seat.Section);