CREATE PROCEDURE InsertCampaignWithRandomOrgs\n    @campaignName NVARCHAR(255),\n    @campaignDate DATETIME\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    -- Declare a table variable to hold the random organizations\n    DECLARE @RandomOrgs TABLE (\n        RowNum INT IDENTITY(1,1),\n        OrgKey INT,\n        Organisation_Name NVARCHAR(255)\n    );\n\n    -- Insert 5 random organizations into the table variable\n    INSERT INTO @RandomOrgs (OrgKey, Organisation_Name)\n    SELECT TOP 5 Key, organisation_name\n    FROM Tbl_Organisations\n    ORDER BY NEWID();\n\n    -- Declare variables to hold organization keys\n    DECLARE @org1 INT, @org2 INT, @org3 INT, @org4 INT, @org5 INT;\n\n    -- Fetch the organization keys from the table variable\n    SELECT \n        @org1 = CASE WHEN RowNum = 1 THEN OrgKey END,\n        @org2 = CASE WHEN RowNum = 2 THEN OrgKey END,\n        @org3 = CASE WHEN RowNum = 3 THEN OrgKey END,\n        @org4 = CASE WHEN RowNum = 4 THEN OrgKey END,\n        @org5 = CASE WHEN RowNum = 5 THEN OrgKey END\n    FROM @RandomOrgs;\n\n    -- Insert the new campaign with the random organizations into Tbl_Campaigns\n    INSERT INTO Tbl_Campaigns (name, date, organisation_1, organisation_2, organisation_3, organisation_4, organisation_5)\n    VALUES (@campaignName, @campaignDate, @org1, @org2, @org3, @org4, @org5);\nEND