-- Drop the existing primary key\nALTER TABLE your_table DROP PRIMARY KEY;\n\n-- Add a new auto-incrementing column\nALTER TABLE your_table ADD COLUMN new_id INT AUTO_INCREMENT PRIMARY KEY FIRST;\n\n-- Update the primary key to the new_id (optional, if you want the old ID values gone)\nUPDATE your_table SET id = new_id;\n\n-- Drop the temporary column if the old ID is now irrelevant\nALTER TABLE your_table DROP COLUMN id;