-- Rename the existing primary key column temporarily\nALTER TABLE your_table RENAME COLUMN id TO old_id;\n\n-- Add a new auto-incrementing column as the primary key\nALTER TABLE your_table ADD COLUMN id SERIAL PRIMARY KEY;\n\n-- Update the old_id with the new id if needed for reference (not always necessary)\nUPDATE your_table SET old_id = id;\n\n-- If you don't need the old_id, drop it\nALTER TABLE your_table DROP COLUMN old_id;