-- Declare the variables\nDEFINE schemaname VARCHAR(128);\nLET schemaname = 'dba'; -- Update with your schema name\n\nDEFINE tablename VARCHAR(128);\nDEFINE sqlstmt VARCHAR(256);\n\nFOREACH cur_tables WITH CURSOR FOR \n    SELECT tabname INTO tablename FROM systables WHERE owner = schemaname AND tabtype = 'T'\nDO\n    LET sqlstmt = "GRANT SELECT ON " || schemaname || "." || tablename || " TO someuser AS dba";\n    EXECUTE IMMEDIATE sqlstmt;\nEND FOREACH;\n\nMESSAGE 'All select grants completed.' TO CLIENT;