-- First day of the current week (assuming Sunday is the first day of the week)\nSELECT DATEADD(d, 1 - DATEPART(dw, GETDATE()), CAST(GETDATE() AS date)) AS FirstDayOfCurrentWeek\n\n-- First day of the last week (assuming Sunday is the first day of the week)\nSELECT DATEADD(d, 1 - DATEPART(dw, DATEADD(wk, -1, GETDATE())), CAST(DATEADD(wk, -1, GETDATE()) AS date)) AS FirstDayOfLastWeek