DECLARE @NewId UNIQUEIDENTIFIER;\nSET @NewId = NEWID(); -- Generate a new unique identifier\n\n-- Insert into the same table by specifying all necessary columns\nINSERT INTO MyTable (uniqueId, PreviousId, Column1, Column2, ...) -- specify your actual column names here\nSELECT \n    @NewId AS uniqueId, -- use the newly generated unique identifier\n    originalRow.uniqueId AS PreviousId, -- set PreviousId to the original row's uniqueId\n    originalRow.Column1,\n    originalRow.Column2,\n    ... -- continue listing fields you want from the original row\nFROM \n    MyTable AS originalRow\nWHERE \n    originalRow.uniqueId = @Id;  -- filter by your source record's unique identifier