-- Trigger after insert\nDELIMITER $$\nCREATE TRIGGER after_user_insert\nAFTER INSERT ON user\nFOR EACH ROW\nBEGIN\n    INSERT INTO changelog_users (user_id, operation, data)\n    VALUES (NEW.id, 'I', JSON_OBJECT('username', NEW.username));\nEND$$\nDELIMITER ;\n\n-- Trigger after update\nDELIMITER $$\nCREATE TRIGGER after_user_update\nAFTER UPDATE ON user\nFOR EACH ROW\nBEGIN\n    INSERT INTO changelog_users (user_id, operation, data)\n    VALUES (NEW.id, 'U', JSON_OBJECT('username', NEW.username));\nEND$$\nDELIMITER ;\n\n-- Trigger after delete\nDELIMITER $$\nCREATE TRIGGER after_user_delete\nAFTER DELETE ON user\nFOR EACH ROW\nBEGIN\n    INSERT INTO changelog_users (user_id, operation, data)\n    VALUES (OLD.id, 'D', NULL);\nEND$$\nDELIMITER ;