WITH RankedResourceValues AS (\n    SELECT \n        pe.prodtree_element_name_l,\n        rs.resource_value,\n        ROW_NUMBER() OVER (PARTITION BY pe.prodtree_element_name_l ORDER BY (SELECT NULL)) AS rn\n    FROM \n        prodtree_element pe\n    LEFT JOIN \n        resource_shortstrings rs ON pe.prodtree_element_name_l_rk = rs.resource_key\n    WHERE \n        rs.language_id = '5'\n        AND pe.prodtree_element_name_l <> ''\n)\nSELECT prodtree_element_name_l, resource_value\nFROM RankedResourceValues\nWHERE rn = 1;