DECLARE @cmd NVARCHAR(1000)\nDECLARE @filename VARCHAR(256)\n\n-- Define the filename and path where to save the XML file.\nSET @filename = 'C:\output\queryresults.xml'\n\n-- Construct the bcp command.\nSET @cmd = N'bcp "SELECT * FROM your_table FOR XML PATH(''Row''), ROOT(''Rows'')" queryout "' + \n           @filename + '" -c -T'\n\n-- Execute the command to export data to an XML file.\nEXEC master..xp_cmdshell @cmd