CREATE FUNCTION dbo.GetUserInfo (@user_pk INT)\n   RETURNS TABLE\n   AS\n   RETURN (\n       SELECT id\n       FROM ice.dbo.[user]\n       WHERE user_pk = @user_pk\n   );\n\n   -- Usage\n   SELECT \n       creator.id,\n       updater.id,\n       owner.id,\n       a.[name]\n   FROM asset a\n   LEFT JOIN dbo.GetUserInfo(a.created_by) AS creator ON 1=1\n   LEFT JOIN dbo.GetUserInfo(a.updated_by) AS updater ON 1=1\n   LEFT JOIN dbo.GetUserInfo(a.owned_by) AS owner ON 1=1;