-- Check if the current user has BACKUP DATABASE permission on the database\nIF NOT EXISTS (\n    SELECT 1 \n    FROM fn_my_permissions(NULL, 'SERVER') AS perm\n    WHERE perm.permission_name = 'BACKUP DATABASE'\n)\nBEGIN\n    RAISERROR ('The user does not have sufficient permissions to perform backup operations.', 16, 1);\n    RETURN;\nEND;\n\n-- Proceed with your script...