-- Numeric ID Example\nCREATE TABLE athlete_numeric (\n    id INT PRIMARY KEY,\n    name VARCHAR(255),\n    teamid INT -- assuming teamid corresponds to numeric id in team table\n);\n\nCREATE TABLE team_numeric (\n    teamid INT PRIMARY KEY,\n    teamname VARCHAR(255)\n);\n\n-- Character ID Example\nCREATE TABLE athlete_char (\n    id INT PRIMARY KEY,\n    name VARCHAR(255),\n    teamid CHAR(3) -- using character code for team\n);\n\nCREATE TABLE team_char (\n    teamid CHAR(3) PRIMARY KEY, -- this should match the IDs used in athlete_char table\n    teamname VARCHAR(255)\n);