DECLARE @TableName SYSNAME = 'DelMe';\nDECLARE @SQL NVARCHAR(MAX) = N'';\n\n-- Create a cursor to iterate over foreign keys referencing DelMe.ID\nDECLARE FKCursor CURSOR LOCAL FORWARD_ONLY READ_ONLY FOR\nSELECT \n    t.name AS TableName,\n    k.NAME AS ForeignKeyName,\n    c.name AS ColumnName\nFROM sys.foreign_keys f\nJOIN sys.tables t ON t.object_id = f.parent_object_id\nJOIN sys.schemas s ON t.schema_id = s.schema_id\nJOIN sys.foreign_key_columns fc ON f.object_id = fc.constraint_object_id\nJOIN sys.columns c ON fc.parent_column_id = c.column_id AND c.object_id = fc.parent_object_id\nWHERE OBJECT_NAME(f.referenced_object_id) = @TableName;\n\nDECLARE @Table SYSNAME, @Key SYSNAME, @Col SYSNAME;\nOPEN FKCursor;\n\nFETCH NEXT FROM FKCursor INTO @Table, @Key, @Col;\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL += N'-- ' + @Table + '.' + @Key + ':' + @Col + CHAR(13) +\n                N'DELETE FROM [' + @Table + '] WHERE [' + @Col + '] IN (SELECT ID FROM DeLMe);' + CHAR(13);\n    \n    FETCH NEXT FROM FKCursor INTO @Table, @Key, @Col;\nEND;\n\nCLOSE FKCursor;\nDEALLOCATE FKCursor;\n\n-- Output the SQL statements\nPRINT @SQL;