-- Table to hold all users\nCREATE TABLE Users (\n    UserID INT PRIMARY KEY,\n    -- other user fields\n);\n\n-- Table to hold all departments\nCREATE TABLE Departments (\n    DepartmentID INT PRIMARY KEY,\n    -- other department fields\n);\n\n-- Table to hold applications\nCREATE TABLE Applications (\n    ApplicationID INT PRIMARY KEY,\n    Name NVARCHAR(255) NOT NULL\n);\n\n-- Permissions for individual users\nCREATE TABLE UserPermissions (\n    PermissionID INT PRIMARY KEY IDENTITY(1,1),\n    UserID INT FOREIGN KEY REFERENCES Users(UserID),\n    ApplicationID INT FOREIGN KEY REFERENCES Applications(ApplicationID),\n    AccessLevel NVARCHAR(20) -- or as appropriate (can be an int mapping to access settings)\n);\n\n-- Permissions for departments\nCREATE TABLE DepartmentPermissions (\n    PermissionID INT PRIMARY KEY IDENTITY(1,1),\n    DepartmentID INT FOREIGN KEY REFERENCES Departments(DepartmentID),\n    ApplicationID INT FOREIGN KEY REFERENCES Applications(ApplicationID),\n    AccessLevel NVARCHAR(20) -- or as appropriate (can be an int mapping to access settings)\n);