CREATE FUNCTION dbo.ConcatenateIDs \n(\n    @separator VARCHAR(10),\n    @GroupID INT\n)\nRETURNS VARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @ResultingString VARCHAR(MAX)\n\n    SELECT @ResultingString =\n        COALESCE(@ResultingString + @separator, '') + ProjID\n    FROM project_members\n    WHERE EmpName = (SELECT EmpName FROM project_members WHERE projID=@GroupID)\n\n    RETURN @ResultingString\nEND