DECLARE @spName NVARCHAR(128)\nDECLARE @dropScript NVARCHAR(MAX) = ''\nDECLARE @createScript NVARCHAR(MAX) = ''\n\n-- Get the names of the top 5 most recently modified stored procedures\nSELECT TOP (5) name INTO #RecentSPs\nFROM sys.procedures\nWHERE modify_date >= '2023-10-01' -- adjust this date as necessary\nORDER BY modify_date DESC\n\n-- Loop through each procedure to generate DROP and CREATE scripts\nDECLARE spCursor CURSOR FOR SELECT name FROM #RecentSPs\nOPEN spCursor\nFETCH NEXT FROM spCursor INTO @spName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Generate DROP script\n    SET @dropScript += 'DROP PROCEDURE [' + @spName + '];' + CHAR(13) + CHAR(10)\n\n    -- Use sp_helptext to get the CREATE procedure script (Note: This is a workaround)\n    DECLARE @definition NVARCHAR(MAX)\n    DECLARE @objId INT = OBJECT_ID(@spName)\n    SET @definition = ''\n\n    SELECT @definition += text\n    FROM sys.sql_modules\n    WHERE object_id = @objId\n\n    SET @createScript += 'GO' + CHAR(13) + CHAR(10) + @definition + 'GO' + CHAR(13) + CHAR(10)\n    \n    FETCH NEXT FROM spCursor INTO @spName\nEND\nCLOSE spCursor\nDEALLOCATE spCursor\n\n-- Combine DROP and CREATE scripts\nPRINT @dropScript + CHAR(13) + CHAR(10) + '-- Creating Procedures --' + CHAR(13) + CHAR(10) + @createScript\n\n-- Clean up the temporary table\nDROP TABLE #RecentSPs