-- Example conversion in a PL/SQL block\nDECLARE\n    start_date DATE := TO_DATE('2023-09-29', 'YYYY-MM-DD'); -- Input date\n    end_date   DATE := TO_DATE('2023-10-01', 'YYYY-MM-DD'); -- Input date\n\n    -- Convert to timestamps for storage\n    start_timestamp TIMESTAMP WITH TIME ZONE;\n    end_timestamp TIMESTAMP WITH TIME ZONE;\nBEGIN\n    start_timestamp := CAST(TO_TIMESTAMP_TZ(start_date || '+00:00', 'YYYY-MM-DD+HH24:MI') AS TIMESTAMP WITH TIME ZONE);\n    end_timestamp := CAST(TO_TIMESTAMP_TZ(end_date || '+00:00', 'YYYY-MM-DD+HH24:MI') AS TIMESTAMP WITH TIME ZONE) + INTERVAL '1' DAY;\n\n    -- Output the timestamps\n    DBMS_OUTPUT.PUT_LINE('Start Date in UTC: ' || start_timestamp);\n    DBMS_OUTPUT.PUT_LINE('End Date in UTC: ' || end_timestamp);\n\n    -- Example usage: Checking if a given date lies within the range\n    IF SYSTIMESTAMP >= start_timestamp AND SYSTIMESTAMP < end_timestamp THEN\n        DBMS_OUTPUT.PUT_LINE('Current system time is within the specified range.');\n    ELSE\n        DBMS_OUTPUT.PUT_LINE('Current system time is not within the specified range.');\n    END IF;\nEND;\n/