SELECT \n    CASE \n        WHEN EmployerCount = 1 THEN 1\n        WHEN EmployerCount BETWEEN 2 AND 9 THEN 2\n        WHEN EmployerCount BETWEEN 10 AND 19 THEN 3\n        -- Add more cases as needed\n        ELSE 4 -- default or other ranges\n    END AS SortOrder,\n    CASE \n        WHEN EmployerCount = 1 THEN '1 employer'\n        WHEN EmployerCount BETWEEN 2 AND 9 THEN '2-9 employers'\n        WHEN EmployerCount BETWEEN 10 AND 19 THEN '10-19 employers'\n        -- Add more cases as needed\n        ELSE 'Other' -- default or other ranges\n    END AS EmployerRange,\n    COUNT(CustomerID) AS CustomerCount\nFROM YourTable\nGROUP BY CASE \n            WHEN EmployerCount = 1 THEN 1\n            WHEN EmployerCount BETWEEN 2 AND 9 THEN 2\n            WHEN EmployerCount BETWEEN 10 AND 19 THEN 3\n            ELSE 4 -- default or other ranges\n        END,\n         CASE \n            WHEN EmployerCount = 1 THEN '1 employer'\n            WHEN EmployerCount BETWEEN 2 AND 9 THEN '2-9 employers'\n            WHEN EmployerCount BETWEEN 10 AND 19 THEN '10-19 employers'\n            ELSE 'Other' -- default or other ranges\n         END