CREATE TABLE #temp (\n    RowID int IDENTITY(1,1) PRIMARY KEY,\n    id int,\n    descr varchar(50)\n);\n\nINSERT INTO #temp (id, descr)\nSELECT id, descr \nFROM mytable \nORDER BY id; -- or whatever order you need\n\nSELECT \n    CASE \n        WHEN t.RowID <= 4 THEN CHAR(64 + t.RowID) \n        ELSE '' \n    END AS SequentialChar,\n    t.id, \n    t.descr\nFROM #temp t;\n\nDROP TABLE #temp;