WITH EmployeeSales AS (\n    SELECT \n        o.EMPLOYEE AS EmployeeID,\n        SUM(o.Quantity * p.Price) AS SalesVolume\n    FROM \n        Orders o\n    JOIN \n        Products p ON o.PRODUCTID = p.ID#\n    GROUP BY \n        o.EMPLOYEE\n),\nAverageSales AS (\n    SELECT \n        AVG(SalesVolume) AS AvgSalesVolume\n    FROM \n        EmployeeSales\n)\nSELECT \n    es.EmployeeID,\n    es.SalesVolume\nFROM \n    EmployeeSales es, AverageSales asvs\nWHERE \n    es.SalesVolume < asvs.AvgSalesVolume;