WITH ClientDeliveries AS (\n    SELECT COUNT(*) AS delivery_count, clientid \n    FROM deliveries \n    GROUP BY clientid\n),\nClientActions AS (\n    SELECT COUNT(*) AS action_count, clientid \n    FROM routeactions \n    GROUP BY clientid\n)\nSELECT \n    cd.clientid,\n    cd.delivery_count,\n    ca.action_count\nFROM \n    ClientDeliveries cd\nINNER JOIN \n    ClientActions ca\nON \n    cd.clientid = ca.clientid;