CREATE TABLE timeline (\n    id SERIAL PRIMARY KEY,\n    content_type VARCHAR(255) NOT NULL,  -- e.g., 'news', 'events'\n    primary_key_id INT NOT NULL,\n    data JSONB NOT NULL,                  -- Serialized version of the data\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    modified_at TIMESTAMP,\n    draft_date TIMESTAMP,\n    is_draft BOOLEAN DEFAULT FALSE,\n    approved_by_user_id INT,\n    approval_status VARCHAR(50)           -- e.g., 'pending', 'approved', 'rejected'\n);\n\n-- Indexes for faster queries\nCREATE INDEX idx_content_type ON timeline(content_type);\nCREATE INDEX idx_primary_key_id ON timeline(primary_key_id);