-- Open existing symmetric key\n     OPEN SYMMETRIC KEY YourSymmetricKey DECRYPTION BY PASSWORD = 'OldPassword';\n     \n     -- Decrypt data into a variable or temp table\n     DECLARE @DecryptedData varbinary(MAX);\n     SELECT @DecryptedData = DECRYPTBYKEY(EncryptedColumn)\n     FROM SourceTable;\n\n     -- Open new symmetric key (if created)\n     OPEN SYMMETRIC KEY NewSymmetricKey DECRYPTION BY PASSWORD = 'NewPassword';\n\n     -- Encrypt data with the new key\n     INSERT INTO TargetTable(TargetColumn)\n     VALUES(ENCRYPTBYKEY(KEY_GUID('NewSymmetricKey'), @DecryptedData));\n     \n     -- Close symmetric keys when done\n     CLOSE SYMMETRIC KEY NewSymmetricKey;\n     CLOSE SYMMETRIC KEY YourSymmetricKey;