CREATE PROCEDURE GetProcessedData \n    @InputTableVariable dbo.MyTableType READONLY\nAS\nBEGIN\n\n    -- Insert data into a temporary table for processing\n    SELECT * INTO #TempTable FROM @InputTableVariable;\n\n    -- Perform operations on the data, for example, select all from temp table\n    -- You can modify this section as required\n    SELECT ID, Name, LEN(Name) AS NameLength\n    FROM #TempTable;\nEND\nGO