CREATE TABLE Products (\n    ProductID INT PRIMARY KEY IDENTITY,\n    ProductTypeID INT FOREIGN KEY REFERENCES ProductTypes(ProductTypeID),\n    Description NVARCHAR(MAX)\n);\n\nCREATE TABLE ProductTypes (\n    ProductTypeID INT PRIMARY KEY IDENTITY,\n    TypeName NVARCHAR(50) NOT NULL\n);\n\nCREATE TABLE Attributes (\n    AttributeID INT PRIMARY KEY IDENTITY,\n    ProductTypeID INT FOREIGN KEY REFERENCES ProductTypes(ProductTypeID),\n    AttributeName NVARCHAR(50) NOT NULL\n);\n\nCREATE TABLE ProductAttributes (\n    ProductAttributeID INT PRIMARY KEY IDENTITY,\n    ProductID INT FOREIGN KEY REFERENCES Products(ProductID),\n    AttributeID INT FOREIGN KEY REFERENCES Attributes(AttributeID),\n    AttributeValue NVARCHAR(MAX)\n);