-- Table for live users\nCREATE TABLE live_users (\n    user_id INT PRIMARY KEY,\n    username VARCHAR(50) NOT NULL,\n    password_hash VARCHAR(255) NOT NULL,\n    email VARCHAR(100)\n);\n\n-- Table for demo users\nCREATE TABLE demo_users (\n    user_id INT PRIMARY KEY,\n    username VARCHAR(50) NOT NULL,\n    password_hash VARCHAR(255) NOT NULL,\n    email VARCHAR(100),\n    is_demo BOOLEAN DEFAULT TRUE  -- To identify as demo data explicitly\n);