-- Enable supplemental logging at the database level\nALTER DATABASE ADD SUPPLEMENTAL LOG DATA;\n\n-- Create a capture process\nBEGIN\n  DBMS_CAPTURE_ADM.CREATE_CAPTURE(\n    queue_name         => 'capture_queue',\n    table_name         => 'schema.table_name',\n    change_set_name    => 'my_changeset',\n    start_scn          => DBMS_FLASHBACK.GET_SYSTEM_CHANGE_NUMBER\n  );\nEND;\n/\n\n-- Create a propagation process\nBEGIN\n  DBMS_PROPAGATION_ADM.CREATE_PROPAGATION(\n    queue_name         => 'propagation_queue'\n  );\nEND;\n/\n\n-- Start the capture process\nEXEC DBMS_CAPTURE.START_CAPTURE('capture_queue');